test_that("get_bible_verse works correctly", {
  # Test case 1: Mock a successful response with a single verse
  mock_response_success_single <- function(req) {
    if (grepl("search=Juan\\+3:16", req$url)) {
      httr2::response(
        status_code = 200,
        headers = "Content-Type: text/html",
        body = '
<div class="passage-text">
<div class="passage-content passage-class-0"><div class="version-RVR1960 result-text-style-normal text-html">
 <h3><span id="es-RVR1960-26138" class="text John-3-16">De tal manera amó Dios al mundo</span></h3><p><span class="text John-3-16"><sup class="versenum">16&nbsp;</sup>Porque de tal manera amó Dios al mundo, que ha dado a su Hijo unigénito, para que todo aquel que en él cree, no se pierda, mas tenga vida eterna.</span> </p><a class="full-chap-link" href="/passage/?search=Juan%203&amp;version=RVR1960" title="View Full Chapter">Read full chapter</a>
</div>
<div class="passage-other-trans"><a href="/verse/es/Juan%203%3A16">Juan 3:16 in all Spanish translations</a></div>
</div>
</div>
'
      )
    } else {
      NULL
    }
  }

  httr2::with_mocked_responses(mock_response_success_single, {
    result <- get_bible_verse("Juan 3:16")
    expect_type(result, "character")
    expect_length(result, 1)
    expect_match(result, "> \\*__Juan 3:16__:  Porque de tal manera amó Dios al mundo, que ha dado a su Hijo unigénito, para que todo aquel que en él cree, no se pierda, mas tenga vida eterna.\\*")
  })

  # Test case 2: Mock a successful response with multiple verses in a chapter
  mock_response_success_multiple <- function(req) {
    if (grepl("search=Salmos\\+23", req$url)) {
      httr2::response(
        status_code = 200,
        headers = "Content-Type: text/html",
        body = '
<div class="passage-content passage-class-0"><div class="version-RVR1960 result-text-style-normal text-html">
 <h3><span id="es-RVR1960-14237" class="text Ps-23-1">Jehová es mi pastor</span></h3><h4 class="psalm-title"><span class="text Ps-23-1">Salmo de David.</span></h4><div class="poetry"><p class="verse line"><span class="text Ps-23-1"><span class="chapternum">23&nbsp;</span>Jehová es mi pastor; nada me faltará.</span></p> <p class="verse line"><span id="es-RVR1960-14238" class="text Ps-23-2"><sup class="versenum">2&nbsp;</sup>En lugares de delicados pastos me hará descansar;</span></p><p class="verse line"><span class="text Ps-23-2">Junto a aguas de reposo me pastoreará.<sup class="crossreference" data-cr="#ces-RVR1960-14238A" data-link="(<a href=&quot;#ces-RVR1960-14238A&quot; title=&quot;See cross-reference A&quot;>A</a>)">(<a href="#ces-RVR1960-14238A" title="See cross-reference A">A</a>)</sup></span></p> <p class="verse line"><span id="es-RVR1960-14239" class="text Ps-23-3"><sup class="versenum">3&nbsp;</sup>Confortará mi alma;</span></p><p class="verse line"><span class="text Ps-23-3">Me guiará por sendas de justicia por amor de su nombre.</span></p></div>  <div class="poetry top-1"><p class="verse line"><span id="es-RVR1960-14240" class="text Ps-23-4"><sup class="versenum">4&nbsp;</sup>Aunque ande en valle de sombra de muerte,</span></p><p class="verse line"><span class="text Ps-23-4">No temeré mal alguno, porque tú estarás conmigo;</span></p><p class="verse line"><span class="text Ps-23-4">Tu vara y tu cayado me infundirán aliento.</span></p> <p class="verse line"><span id="es-RVR1960-14241" class="text Ps-23-5"><sup class="versenum">5&nbsp;</sup>Aderezas mesa delante de mí en presencia de mis angustiadores;</span></p><p class="verse line"><span class="text Ps-23-5">Unges mi cabeza con aceite; mi copa está rebosando.</span></p> <p class="verse line"><span id="es-RVR1960-14242" class="text Ps-23-6"><sup class="versenum">6&nbsp;</sup>Ciertamente el bien y la misericordia me seguirán todos los días de mi vida,</span></p><p class="verse line"><span class="text Ps-23-6">Y en la casa de Jehová moraré por largos días.</span></p></div> <div class="crossrefs hidden">
<h4>Cross references</h4><ol><li id="ces-RVR1960-14238A"><a href="#es-RVR1960-14238" title="Go to Salmos 23:2">Salmos 23:2</a> : <a class="crossref-link" href="/passage/?search=Apocalipsis%207%3A17&amp;version=RVR1960" data-bibleref="Apocalipsis 7:17">Ap. 7.17.</a></li>

</ol></div> <!--end of crossrefs-->
</div>
</div>
'
      )
    } else {
      NULL
    }
  }

  httr2::with_mocked_responses(mock_response_success_multiple, {
    result <- get_bible_verse("Salmos 23")
    expect_type(result, "character")
    expect_length(result, 1)
    expect_match(result,
                 "> *__Salmos 23__: ^**1**^ Jehová es mi pastor; nada me faltará. ^**2**^ En lugares de delicados pastos me hará descansar; Junto a aguas de reposo me pastoreará. ^**3**^ Confortará mi alma; Me guiará por sendas de justicia por amor de su nombre. ^**4**^ Aunque ande en valle de sombra de muerte, No temeré mal alguno, porque tú estarás conmigo; Tu vara y tu cayado me infundirán aliento. ^**5**^ Aderezas mesa delante de mí en presencia de mis angustiadores; Unges mi cabeza con aceite; mi copa está rebosando. ^**6**^ Ciertamente el bien y la misericordia me seguirán todos los días de mi vida, Y en la casa de Jehová moraré por largos días.*",
                 fixed = TRUE)
  })

